fn parse_tables(sql: &str) -> HashSet<String> {
    let mut tables = HashSet::new();

    // Split the SQL statement into individual tokens
    let mut tokens: Vec<_> = sql.split_whitespace().collect();
    let mut prev;

    while !tokens.is_empty() {
