using Xamarin.Forms.Platform.iOS;

[assembly: Xamarin.Forms.ExportRenderer(typeof(CustomRadioButton), typeof(CustomRadioButtonApp.iOS.Renderers.CustomRadioButtonRenderer))]
namespace CustomRadioButtonApp.iOS.Renderers
{
    public class CustomRadioButtonRenderer : RadioButtonRenderer
    {
        protected override void OnElementChanged(ElementChangedEventArgs<RadioButton> e)
        {
            base.OnElementChanged(e);
            if (Control != null)
            {
                UpdateButtonColors();
            }
        }

        private void UpdateButtonColors()
        {
            Control.SetTitleColor(Element.TextColor.ToUIColor(), UIControlState.Normal);
            Control.SetTitleColor(Element.TextColor.ToUIColor(), UIControlState.Selected);
        }
