interface UserContextData {
  user: any;
  isLoading: boolean;
  isError: boolean;
}

const UserContext = createContext<UserContextData>({} as UserContextData);

const useUser = () => {
  const context = useContext(UserContext);
  if (!context) {
    throw new Error('useUser must be used within a UserProvider');
  }
  return context;
};

interface UserProviderProps {
  userId: string;
  children: ReactNode;
}

const UserProvider = ({ children, userId }: PropsWithChildren<UserProviderProps>) => {
  const { data: user, isLoading, isError } = useQuery(['userProfile', userId], () => getUserProfile(userId));

  return (
