    await Task.WhenAll(SortAsync(left), SortAsync(right));

    await MergeAsync(left, right, arr, 0, mid, arr.Length - mid);

    return arr;
}

private static async Task MergeAsync(int[] left, int[] right, int[] arr, int leftStart, int leftEnd, int rightStart)
{
    int i = leftStart, j = rightStart, k = 0;

