using UnityEngine;
using UnityEditor;
using System.Collections;

public class LoadOBJ : MonoBehaviour
{
    // Set the path of your OBJ file here
    public string objFilePath = "path-to-your-obj-file.obj";

    void Start()
    {
        StartCoroutine(LoadObjFromFile());
    }

    IEnumerator LoadObjFromFile()
    {
        // Load the OBJ file asynchronously
        OBJLoader.OBJLoader loader = GetComponent<OBJLoader.OBJLoader>();
