function isCaretAtBottomLine(textarea) {
  // Check if the textarea is focused
  if (document.activeElement !== textarea) {
    return false;
  }

  // Get the current scrolling height and other properties from the textarea
  const { scrollTop, clientHeight, lineHeight } = getTextAreaScrollProperties(textarea);

  // Calculate the current line number based on the textarea's properties
  const caretLineNumber = getCurrentLineNumber(textarea, lineHeight);

  // Calculate the last line number that the user can scroll to
  const lastLineNumber = (scrollTop + clientHeight) / lineHeight;

  // Compare the current line number to the last line number to determine if the cursor is at the last line
  return caretLineNumber >= lastLineNumber;
