#!/bin/bash

# Replace the searchString with the desired issuer string
searchString="Issuer String"

# Replace the bundleFilePath with the path to your cert bundle file
bundleFilePath="path/to/your/cert-bundle.crt"

awk -v search="$searchString" '
  BEGIN { cert = 0; output_block = 0; }

  /-----BEGIN CERTIFICATE-----/ {
    block = 1;
    buffer = $0RS;
    next;
  }

